// src/PDFUpload.js
import React, { useRef, useState } from 'react';

const PDFUpload = ({ onFileAccepted }) => {
    const [dragOver, setDragOver] = useState(false);
    const [selectedFile, setSelectedFile] = useState(null);
    const fileInputRef = useRef(null);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            onFileAccepted(file);
            setSelectedFile(file.name);
        }
    };

    const handleDragOver = (event) => {
        event.preventDefault();
        setDragOver(true);
    };

    const handleDragLeave = () => {
        setDragOver(false);
    };

    const handleDrop = (event) => {
        event.preventDefault();
        setDragOver(false);
        const file = event.dataTransfer.files[0];
        if (file) {
            onFileAccepted(file);
            setSelectedFile(file.name);
        }
    };

    const handleClick = () => {
        fileInputRef.current.click();
    };

    return (
        <div
            style={{
                ...styles.dropZone,
                borderColor: dragOver ? '#dda0dd' : '#4B0082',
                backgroundColor: dragOver ? '#f3e5f5' : 'transparent',
            }}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={handleClick}
            role="button"
            tabIndex="0"
            aria-label="Upload a PDF file"
        >
            <input
                type="file"
                accept="application/pdf"
                ref={fileInputRef}
                style={styles.input}
                onChange={handleFileChange}
            />
            <p>{selectedFile ? `Selected file: ${selectedFile}` : 'Drag and drop a PDF file here, or click to select a file'}</p>
        </div>
    );
};

const styles = {
    dropZone: {
        width: '80%',
        height: '200px',
        border: '2px dashed #4B0082',
        borderRadius: '10px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        margin: '20px auto',
        cursor: 'pointer',
        transition: 'background-color 0.3s, border-color 0.3s',
    },
    input: {
        display: 'none',
    },
};

export default PDFUpload;
