import React, { useRef, useState } from 'react';
import pdfjsLib from 'pdfjs-dist';

const PDFUpload = ({ onFileAccepted }) => {
    const [dragOver, setDragOver] = useState(false);
    const [selectedFile, setSelectedFile] = useState(null);
    const [extractedText, setExtractedText] = useState('');
    const fileInputRef = useRef(null);

    const handleFileChange = async (event) => {
        const file = event.target.files[0];
        if (file && file.type === 'application/pdf') {
            setSelectedFile(file.name);

            const reader = new FileReader();
            reader.onload = async (event) => {
                const buffer = event.target.result;
                const typedArray = new Uint8Array(buffer);
                const pdf = await pdfjsLib.getDocument({ data: typedArray }).promise;
                const page = await pdf.getPage(1); // Change page number as needed
                const textContent = await page.getTextContent();
                const textItems = textContent.items.map((item) => item.str);
                const extractedText = textItems.join(' ');
                setExtractedText(extractedText);
            };
            reader.readAsArrayBuffer(file);

            onFileAccepted(file);
        } else {
            alert('Please upload a PDF file.');
            setSelectedFile(null);
        }
    };

    const handleDragOver = (event) => {
        event.preventDefault();
        setDragOver(true);
    };

    const handleDragLeave = () => {
        setDragOver(false);
    };

    const handleDrop = (event) => {
        event.preventDefault();
        setDragOver(false);
        const file = event.dataTransfer.files[0];
        if (file && file.type === 'application/pdf') {
            handleFileChange({ target: { files: [file] } });
        } else {
            alert('Please upload a PDF file.');
            setSelectedFile(null);
        }
    };

    const handleClick = () => {
        fileInputRef.current.click();
    };

    return (
        <div
            style={{
                ...styles.dropZone,
                borderColor: dragOver ? '#dda0dd' : '#4B0082',
                backgroundColor: dragOver ? '#f3e5f5' : 'transparent',
            }}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            onClick={handleClick}
            role="button"
            tabIndex="0"
            aria-label="Upload a PDF file"
        >
            <input
                type="file"
                accept="application/pdf"
                ref={fileInputRef}
                style={styles.input}
                onChange={handleFileChange}
            />
            <p>{selectedFile ? `Selected file: ${selectedFile}` : 'Drag and drop a PDF file here, or click to select a file'}</p>
            {extractedText && (
                <div>
                    <h3>Extracted Text:</h3>
                    <textarea
                        value={extractedText}
                        style={styles.textArea}
                        rows={10}
                        readOnly
                    />
                </div>
            )}
        </div>
    );
};

const styles = {
    dropZone: {
        width: '80%',
        height: 'auto',
        border: '2px dashed #4B0082',
        borderRadius: '10px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        margin: '20px auto',
        padding: '20px',
        cursor: 'pointer',
        transition: 'background-color 0.3s, border-color 0.3s',
    },
    input: {
        display: 'none',
    },
    textArea: {
        width: '100%',
        marginTop: '10px',
        padding: '10px',
        borderRadius: '5px',
        border: '1px solid #ccc',
        resize: 'none',
    },
};

export default PDFUpload;
