import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './Navbar';
import MainContent from './MainContent';
import PDFUpload from './PDFUpload';
import UploadButton from './UploadButton';
import Footer from './Footer';
import About from './About';
import Contact from './Contact';
import './index.css';

function App() {
  const [file, setFile] = useState(null);
  const [extractedText, setExtractedText] = useState('');

  const handleFileAccepted = (file) => {
    setFile(file);
  };

  const handleUpload = () => {
    if (file) {
      // Handle the file upload here
      console.log('Uploading file:', file);
      // Assuming you set the extracted text in this function
      // For demonstration, I'm setting it here directly
      setExtractedText('Example extracted text');
    } else {
      alert('Please select a file first!');
    }
  };

  return (
    <Router>
      <div style={styles.app}>
        <Navbar />
        <main style={styles.main}>
          <Routes>
            <Route
              path="/"
              element={
                <div style={styles.contentContainer}>
                  <MainContent />
                  <PDFUpload onFileAccepted={handleFileAccepted} />
                  <UploadButton onClick={handleUpload} />
                  {extractedText && (
                    <div style={styles.extractedText}>
                      <h3>Extracted Text:</h3>
                      <p>{extractedText}</p>
                    </div>
                  )}
                </div>
              }
            />
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

const styles = {
  app: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
  },
  main: {
    flex: '1',
    display: 'flex',
    justifyContent: 'center', // Center main content horizontally
    // alignItems: 'center',    // Center main content vertically
  },
  contentContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',   // Center items horizontally
    textAlign: 'center',
  },
  extractedText: {
    marginTop: '1rem',
  },
};

export default App;
